.padding {
  padding: 10px;
}

.month {
  display: flex;
  justify-content: center;
  align-items: center;
  margin: 20px 0px 20px 0px;
}

.month-title {
  text-align: center;
  font-size: x-large;
  flex: 4;
}

.month-year {
  font-size: 0.8em;
  color: #aaa;
}

.month-arrow-left {
  flex: 1;
  width: 24px;
  height: 24px;
}

.month-arrow-right {
  width: 24px;
  height: 24px;
  flex: 1;
}

.footer {
  width: 100%;
  /* min-width: 400px; */
  display: flex;
  justify-content: center;
  align-items: center;
  flex-flow: row wrap;
  text-align: center;
  cursor: default;
}

.noselect {
  -webkit-touch-callout: none; /* iOS Safari */
    -webkit-user-select: none; /* Chrome/Safari/Opera */ /* Konqueror */
       -moz-user-select: none; /* Firefox */
        -ms-user-select: none; /* Internet Explorer/Edge */
            user-select: none;
}

.day-headers {
  flex-basis: 14%;
  justify-content: center;
  align-items: center;
  padding: 8px 0px 8px 0px;
  font-weight: bold;
}


.day {
  flex-basis: 14%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding: 8px 0px 8px 0px;
  position: relative;
}

.day, p {
  padding-left: 8px;
  padding-right: 8px;
}

.current {
  position: absolute;
  top: calc(50% - 15px);
  left: calc(50% - 15px);
  border: 2px solid #336699;
  border-radius: 15px;
  width: 30px;
  height: 30px;
  /* z-index: -80; */
}

.selected {
  position: absolute;
  top: calc(50% - 15px);
  left: calc(50% - 15px);
  background-color: #336699;
  border-radius: 15px;
  width: 30px;
  height: 30px;
  /* z-index: -100; */
}


.non-current {
  opacity: 0.2;
}
